// --- lib_netio.FDb.ind_var.InsertMaybe
// Insert row into hash table. Return true if row is reachable through the hash after the function completes.
bool lib_netio::ind_var_InsertMaybe(lib_netio::FVar& row) {
    bool retval = true; // if already in hash, InsertMaybe returns true
    if (LIKELY(row.ind_var_next == (lib_netio::FVar*)-1)) {// check if in hash already
        row.ind_var_hashval = algo::cstring_Hash(0, row.var);
        ind_var_Reserve(1);
        u32 index = row.ind_var_hashval & (_db.ind_var_buckets_n - 1);
        lib_netio::FVar* *prev = &_db.ind_var_buckets_elems[index];
        do {
            lib_netio::FVar* ret = *prev;
            if (!ret) { // exit condition 1: reached the end of the list
                break;
            }
            if ((*ret).var == row.var) { // exit condition 2: found matching key
                retval = false;
                break;
            }
            prev = &ret->ind_var_next;
        } while (true);
        if (retval) {
            row.ind_var_next = *prev;
            _db.ind_var_n++;
            *prev = &row;
        }
    }
    return retval;
}

// --- lib_netio.FDb.ind_var.Remove
// Remove reference to element from hash index. If element is not in hash, do nothing
void lib_netio::ind_var_Remove(lib_netio::FVar& row) {
    if (LIKELY(row.ind_var_next != (lib_netio::FVar*)-1)) {// check if in hash already
        u32 index = row.ind_var_hashval & (_db.ind_var_buckets_n - 1);
        lib_netio::FVar* *prev = &_db.ind_var_buckets_elems[index]; // addr of pointer to current element
        while (lib_netio::FVar *next = *prev) {                          // scan the collision chain for our element
            if (next == &row) {        // found it?
                *prev = next->ind_var_next; // unlink (singly linked list)
                _db.ind_var_n--;
                row.ind_var_next = (lib_netio::FVar*)-1;// not-in-hash
                break;
            }
            prev = &next->ind_var_next;
        }
    }
}

// --- lib_netio.FDb.ind_var.Reserve
// Reserve enough room in the hash for N more elements. Return success code.
void lib_netio::ind_var_Reserve(int n) {
    ind_var_AbsReserve(_db.ind_var_n + n);
}

// --- lib_netio.FDb.ind_var.AbsReserve
// Reserve enough room for exacty N elements. Return success code.
void lib_netio::ind_var_AbsReserve(int n) {
    u32 old_nbuckets = _db.ind_var_buckets_n;
    u32 new_nelems   = n;
    // # of elements has to be roughly equal to the number of buckets
    if (new_nelems > old_nbuckets) {
        int new_nbuckets = i32_Max(algo::BumpToPow2(new_nelems), u32(4));
        u32 old_size = old_nbuckets * sizeof(lib_netio::FVar*);
        u32 new_size = new_nbuckets * sizeof(lib_netio::FVar*);
        // allocate new array. we don't use Realloc since copying is not needed and factor of 2 probably
        // means new memory will have to be allocated anyway
        lib_netio::FVar* *new_buckets = (lib_netio::FVar**)algo_lib::malloc_AllocMem(new_size);
        if (UNLIKELY(!new_buckets)) {
            FatalErrorExit("lib_netio.out_of_memory  field:lib_netio.FDb.ind_var");
        }
        memset(new_buckets, 0, new_size); // clear pointers
        // rehash all entries
        for (int i = 0; i < _db.ind_var_buckets_n; i++) {
            lib_netio::FVar* elem = _db.ind_var_buckets_elems[i];
            while (elem) {
                lib_netio::FVar &row        = *elem;
                lib_netio::FVar* next       = row.ind_var_next;
                u32 index          = row.ind_var_hashval & (new_nbuckets-1);
                row.ind_var_next     = new_buckets[index];
                new_buckets[index] = &row;
                elem               = next;
            }
        }
        // free old array
        algo_lib::malloc_FreeMem(_db.ind_var_buckets_elems, old_size);
        _db.ind_var_buckets_elems = new_buckets;
        _db.ind_var_buckets_n = new_nbuckets;
    }
}

