// --- lib_netio.FDb..Init
// Set all fields to initial values.
void lib_netio::FDb_Init() {
    // initialize LAry var (lib_netio.FDb.var)
    _db.var_n = 0;
    memset(_db.var_lary, 0, sizeof(_db.var_lary)); // zero out all level pointers
    lib_netio::FVar* var_first = (lib_netio::FVar*)algo_lib::malloc_AllocMem(sizeof(lib_netio::FVar) * (u64(1)<<4));
    if (!var_first) {
        FatalErrorExit("out of memory");
    }
    for (int i = 0; i < 4; i++) {
        _db.var_lary[i]  = var_first;
        var_first    += 1ULL<<i;
    }
    // initialize hash table for lib_netio::FVar;
    _db.ind_var_n             	= 0; // (lib_netio.FDb.ind_var)
    _db.ind_var_buckets_n     	= 4; // (lib_netio.FDb.ind_var)
    _db.ind_var_buckets_elems 	= (lib_netio::FVar**)algo_lib::malloc_AllocMem(sizeof(lib_netio::FVar*)*_db.ind_var_buckets_n); // initial buckets (lib_netio.FDb.ind_var)
    if (!_db.ind_var_buckets_elems) {
        FatalErrorExit("out of memory"); // (lib_netio.FDb.ind_var)
    }
    memset(_db.ind_var_buckets_elems, 0, sizeof(lib_netio::FVar*)*_db.ind_var_buckets_n); // (lib_netio.FDb.ind_var)

    lib_netio::InitReflection();
}

