// --- mdbg.FDb.lpool.FreeMem
// Free block of memory previously returned by Lpool.
void mdbg::lpool_FreeMem(void* mem, u64 size) {
    size = u64_Max(size,1ULL<<4);
    u64 cell = algo::u64_BitScanReverse(size-1) + 1 - 4;
    if (mem && cell < 36) {
        lpool_Lpblock *temp = (lpool_Lpblock*)mem; // push  singly linked list
        temp->next = _db.lpool_free[cell];
        _db.lpool_free[cell] = temp;
    }
}

// --- mdbg.FDb.lpool.AllocMem
// Allocate new piece of memory at least SIZE bytes long.
// If not successful, return NULL
// The allocated block is at least 1<<4
// The maximum allocation size is at most 1<<(36+4)
void* mdbg::lpool_AllocMem(u64 size) {
    void *retval = NULL;
    size     = u64_Max(size,1<<4); // enforce alignment
    u64 cell = algo::u64_BitScanReverse(size-1) + 1 - 4;
    if (cell < 36) {
        u64 i    = cell;
        // try to find a block that's at least as large as required.
        // if found, remove from free list
        for (; i < 36; i++) {
            lpool_Lpblock *blk = _db.lpool_free[i];
            if (blk) {
                _db.lpool_free[i] = blk->next;
                retval = blk;
                break;
            }
        }
        // if suitable size block is not found, create a new one
        // by requesting a block from the base allocator.
        if (UNLIKELY(!retval)) {
            i = u64_Max(cell, 21-4); // 2MB min -- allow huge page to be used
            retval = algo_lib::sbrk_AllocMem(1ULL<<(i+4));
        }
        if (LIKELY(retval)) {
            // if block is more than 2x as large as needed, return the upper half to the free
            // list (repeatedly). meanwhile, retval doesn't change.
            while (i > cell) {
                i--;
                int half = 1ULL<<(i+4);
                lpool_Lpblock *blk = (lpool_Lpblock*)((u8*)retval + half);
                blk->next = _db.lpool_free[i];
                _db.lpool_free[i] = blk;
            }
        }
    }
    return retval;
}

// --- mdbg.FDb.lpool.ReserveBuffers
// Add N buffers of some size to the free store
// Reserve NBUF buffers of size BUFSIZE from the base pool (algo_lib::sbrk)
bool mdbg::lpool_ReserveBuffers(u64 nbuf, u64 bufsize) {
    bool retval = true;
    bufsize = u64_Max(bufsize, 1<<4);
    u64 cell = algo::u64_BitScanReverse(bufsize-1) + 1 - 4;
    if (cell < 36) {
        for (u64 i = 0; i < nbuf; i++) {
            u64 size = 1ULL<<(cell+4);
            lpool_Lpblock *temp = (lpool_Lpblock*)algo_lib::sbrk_AllocMem(size);
            if (temp == NULL) {
                retval = false;
                break;// why continue?
            } else {
                temp->next = _db.lpool_free[cell];
                _db.lpool_free[cell] = temp;
            }
        }
    }
    return retval;
}

// --- mdbg.FDb.lpool.ReallocMem
// Allocate new block, copy old to new, delete old.
// If the new size is same as old size, do nothing.
// In all other cases, new memory is allocated (i.e. size reduction is not a no-op)
// If no memory, return NULL; old memory remains untouched
void* mdbg::lpool_ReallocMem(void* oldmem, u64 old_size, u64 new_size) {
    void *ret = oldmem;
    if (new_size != old_size) {
        ret = lpool_AllocMem(new_size);
        if (ret && oldmem) {
            memcpy(ret,oldmem,u64_Min(new_size,old_size));
            lpool_FreeMem(oldmem, old_size);
        }
    }
    return ret;
}

// --- mdbg.FDb.lpool.Alloc
// Allocate memory for new default row.
// If out of memory, process is killed.
u8& mdbg::lpool_Alloc() {
    u8* row = lpool_AllocMaybe();
    if (UNLIKELY(row == NULL)) {
        FatalErrorExit("mdbg.out_of_mem  field:mdbg.FDb.lpool  comment:'Alloc failed'");
    }
    return *row;
}

// --- mdbg.FDb.lpool.AllocMaybe
// Allocate memory for new element. If out of memory, return NULL.
u8* mdbg::lpool_AllocMaybe() {
    u8 *row = (u8*)lpool_AllocMem(sizeof(u8));
    if (row) {
        new (row) u8; // call constructor
    }
    return row;
}

// --- mdbg.FDb.lpool.Delete
// Remove row from all global and cross indices, then deallocate row
void mdbg::lpool_Delete(u8 &row) {
    int length = sizeof(u8);
    lpool_FreeMem(&row, length);
}

// --- mdbg.FDb._db.ReadArgv
// Read argc,argv directly into the fields of the command line(s)
// The following fields are updated:
//     mdbg.FDb.cmdline
//     algo_lib.FDb.cmdline
void mdbg::ReadArgv() {
    command::mdbg &cmd = mdbg::_db.cmdline;
    algo_lib::Cmdline &base = algo_lib::_db.cmdline;
    int needarg=-1;// unknown
    int argidx=1;// skip process name
    int anonidx=0;
    algo::strptr nextanon = command::mdbg_GetAnon(cmd, anonidx);
    tempstr err;
    algo::strptr attrname;
    bool isanon=false; // true if attrname is anonfld (positional)
    algo_lib::FieldId baseattrid;
    command::FieldId attrid;
    bool endopt=false;
    int whichns=0;// which namespace does the current attribute belong to
    bool target_present = false;
    for (; argidx < algo_lib::_db.argc; argidx++) {
        algo::strptr arg = algo_lib::_db.argv[argidx];
        algo::strptr attrval;
        algo::strptr dfltval;
        bool haveval=false;
        bool dash=elems_N(arg)>1 && arg.elems[0]=='-'; // a single dash is not an option
        // this attribute is a value
        if (endopt || needarg>0 || !dash) {
            attrval=arg;
            haveval=true;
        } else {
            // this attribute is a field name (with - or --)
            // or a -- by itself
            bool dashdash = elems_N(arg) >= 2 && arg.elems[1]=='-';
            int skip = int(dash) + dashdash;
            attrname=ch_RestFrom(arg,skip);
            if (skip==2 && elems_N(arg)==2) {
                endopt=true;
                continue;// nothing else to do here
            }
            // parse "-a:B" arg into attrname,attrvalue
            algo::i32_Range colon = TFind(attrname,':');
            if (colon.beg < colon.end) {
                attrval=ch_RestFrom(attrname,colon.end);
                attrname=ch_FirstN(attrname,colon.beg);
                haveval=true;
            }
            // look up which command (this one or the base) contains the field
            whichns=0;
            needarg=-1;
            // look up parameter information in base namespace (needarg will be -1 if lookup fails)
            if (algo_lib::FieldId_ReadStrptrMaybe(baseattrid,attrname)) {
                needarg = algo_lib::Cmdline_NArgs(baseattrid,dfltval,&isanon);
            }
            if (needarg<0) {
                whichns=1;
                // look up parameter information in this namespace (needarg will be -1 if lookup fails)
                if (command::FieldId_ReadStrptrMaybe(attrid,attrname)) {
                    needarg = command::mdbg_NArgs(attrid,dfltval,&isanon);
                }
            }
            if (attrval == "" && dfltval != "") {
                attrval=dfltval;
                haveval=true;
            }
            if (needarg<0) {
                err<<"mdbg: unknown option "<<Keyval("value",arg)<<eol;
            } else {
                if (isanon) {
                    if (attrname == nextanon) { // treat named anon (positional) argument as unnamed
                        attrname = ""; // treat it as unnamed
                    } else if (nextanon != "") { // disallow out-of-order anon (positional) args
                        err<<"mdbg: error at "<<algo::strptr_ToSsim(arg)<<": must be preceded by [-"<<nextanon<<"]"<<eol;
                    }
                }
            }
        }
        // look up anon field name based on index
        // anon fields are only allowed in the leaf ns, never base
        if (ch_N(attrname) == 0) {
            attrname = nextanon;
            nextanon = command::mdbg_GetAnon(cmd, ++anonidx);
            command::FieldId_ReadStrptrMaybe(attrid,attrname);
            whichns=1;
        }
        if (ch_N(attrname) == 0) {
            err << "mdbg: too many arguments. error at "<<algo::strptr_ToSsim(arg)<<eol;
        }
        // read value into currently selected arg
        if (haveval) {
            bool ret=false;
            // it's already known which namespace is consuming the args,
            // so directly go there
            if (whichns == 0) {
                ret=algo_lib::Cmdline_ReadFieldMaybe(base, attrname, attrval);
            }
            if (whichns==1) {
                ret=command::mdbg_ReadFieldMaybe(cmd, attrname, attrval);
                switch(attrid.value) {
                    case command_FieldId_target: target_present=true; break;
                    default:break;
                }
            }
            if (!ret) {
                err<<"mdbg: error in "
                <<Keyval("option",attrname)
                <<Keyval("value",attrval)<<eol;
            }
            needarg--;
            if (needarg <= 0) {
                attrname="";// forget which argument was being filled
            }
        }
    }
    bool dohelp = false;
    bool doexit=false;
    if (algo_lib::_db.cmdline.help) {
        dohelp = true;
        doexit = true;
        algo_lib::_db.exit_code = 1; // help exits with non-zero status code
    } else if (algo_lib::_db.cmdline.version) {
        // -ver output goes to stdout
        prlog(algo::gitinfo_Get());
        doexit = true;
    } else if (algo_lib::_db.cmdline.signature) {
        ind_beg(algo_lib::_db_dispsigcheck_curs,dispsigcheck,algo_lib::_db) {
            // dispsig goes to stdout
            dmmeta::Dispsigcheck out;
            dispsigcheck_CopyOut(dispsigcheck,out);
            prlog(out);
        }ind_end
        doexit = true;
    }
    if (!dohelp) {
        if (!target_present) {
            err << "mdbg: Missing value for required argument -target (see -help)" << eol;
            doexit = true;
        }
    }
    // dmmeta.floadtuples:mdbg.FDb.cmdline
    if (!dohelp && err=="") {
        algo_lib::ResetErrtext();
        if (!mdbg::LoadTuplesMaybe(cmd.in,true)) {
            err << "mdbg.load_input  "<<algo_lib::DetachBadTags()<<eol;
        }
    }
    if (err != "") {
        algo_lib::_db.exit_code=1;
        prerr(err);
        doexit=true;
    }
    if (dohelp) {
        prlog(mdbg_help);
    }
    if (doexit) {
        _exit(algo_lib::_db.exit_code);
    }
    algo_lib::ResetErrtext();
}

// --- mdbg.FDb._db.MainLoop
// Main loop.
void mdbg::MainLoop() {
    algo::SchedTime time(algo::get_cycles());
    algo_lib::_db.clock          = time;
    do {
        algo_lib::_db.next_loop.value = algo_lib::_db.limit;
        mdbg::Steps();
    } while (algo_lib::_db.next_loop < algo_lib::_db.limit);
}

