// Main step
void mdbg::Step() {
}

// --- mdbg.FDb._db.InitReflection
// Load statically available data into tables, register tables and database.
static void mdbg::InitReflection() {
    algo_lib::imdb_InsertMaybe(algo::Imdb("mdbg", mdbg::InsertStrptrMaybe, NULL, mdbg::MainLoop, NULL, algo::Comment()));

    algo::Imtable t_trace;
    t_trace.imtable         = "mdbg.trace";
    t_trace.ssimfile        = "";
    t_trace.size            = sizeof(mdbg::trace);
    t_trace.comment.value   = "";
    t_trace.c_RowidFind     = trace_RowidFind;
    t_trace.NItems          = trace_N;
    t_trace.Print           = (algo::ImrowPrintFcn)mdbg::trace_Print;
    algo_lib::imtable_InsertMaybe(t_trace);


    // -- load signatures of existing dispatches --
    algo_lib::InsertStrptrMaybe("dmmeta.Dispsigcheck  dispsig:'mdbg.Input'  signature:'a39a7508119d6dc51451d2cda9f120fd888b1184'");
}

// --- mdbg.FDb._db.InsertStrptrMaybe
// Parse strptr into known type and add to database.
// Return value is true unless an error occurs. If return value is false, algo_lib::_db.errtext has error text
bool mdbg::InsertStrptrMaybe(algo::strptr str) {
    bool retval = true;
    mdbg::TableId table_id(-1);
    value_SetStrptrMaybe(table_id, algo::GetTypeTag(str));
    switch (value_GetEnum(table_id)) {
        case mdbg_TableId_dev_Cfg: { // finput:mdbg.FDb.cfg
            dev::Cfg elem;
            retval = dev::Cfg_ReadStrptrMaybe(elem, str);
            retval = retval && cfg_InputMaybe(elem);
            break;
        }
        case mdbg_TableId_dev_Builddir: { // finput:mdbg.FDb.builddir
            dev::Builddir elem;
            retval = dev::Builddir_ReadStrptrMaybe(elem, str);
            retval = retval && builddir_InputMaybe(elem);
            break;
        }
        default:
        break;
    } //switch
    if (!retval) {
        algo_lib::NoteInsertErr(str); // increment error counter
    }
    return retval;
}

// --- mdbg.FDb._db.LoadTuplesMaybe
// Load all finputs from given directory.
bool mdbg::LoadTuplesMaybe(algo::strptr root, bool recursive) {
    bool retval = true;
    if (FileQ(root)) {
        retval = mdbg::LoadTuplesFile(root, recursive);
    } else if (root == "-") {
        retval = mdbg::LoadTuplesFd(algo::Fildes(0),"(stdin)",recursive);
    } else if (DirectoryQ(root)) {
        retval = retval && mdbg::LoadTuplesFile(algo::SsimFname(root,"dmmeta.dispsigcheck"),recursive);
        retval = retval && mdbg::LoadTuplesFile(algo::SsimFname(root,"dev.cfg"),recursive);
        retval = retval && mdbg::LoadTuplesFile(algo::SsimFname(root,"dev.builddir"),recursive);
    } else {
        algo_lib::SaveBadTag("path", root);
        algo_lib::SaveBadTag("comment", "Wrong working directory?");
        retval = false;
    }
    return retval;
}

// --- mdbg.FDb._db.LoadTuplesFile
// Load all finputs from given file.
// Read tuples from file FNAME into this namespace's in-memory database.
// If RECURSIVE is TRUE, then also load these tuples into any parent namespaces
// It a file referred to by FNAME is missing, no error is reported (it's considered an empty set).
// Function returns TRUE if all records were parsed and inserted without error.
// If the function returns FALSE, use algo_lib::DetachBadTags() for error description
bool mdbg::LoadTuplesFile(algo::strptr fname, bool recursive) {
    bool retval = true;
    algo_lib::FFildes fildes;
    // missing files are not an error
    fildes.fd = OpenRead(fname,algo::FileFlags());
    if (ValidQ(fildes.fd)) {
        retval = LoadTuplesFd(fildes.fd, fname, recursive);
    }
    return retval;
}

// --- mdbg.FDb._db.LoadTuplesFd
// Load all finputs from given file descriptor.
bool mdbg::LoadTuplesFd(algo::Fildes fd, algo::strptr fname, bool recursive) {
    bool retval = true;
    ind_beg(algo::FileLine_curs,line,fd) {
        if (recursive) {
            retval = retval && algo_lib::InsertStrptrMaybe(line);
        }
        retval = retval && mdbg::InsertStrptrMaybe(line);
        if (!retval) {
            algo_lib::_db.errtext << eol
            << fname << ":"
            << (ind_curs(line).i+1)
            << ": " << line << eol;
            break;
        }
    }ind_end;
    return retval;
}

// --- mdbg.FDb._db.LoadSsimfileMaybe
// Load specified ssimfile.
bool mdbg::LoadSsimfileMaybe(algo::strptr fname, bool recursive) {
    bool retval = true;
    if (FileQ(fname)) {
        retval = mdbg::LoadTuplesFile(fname, recursive);
    }
    return retval;
}

