// --- wsdb.FieldId.value.ToCstr
// Convert numeric value of field to one of predefined string constants.
// If string is found, return a static C string. Otherwise, return NULL.
const char* wsdb::value_ToCstr(const wsdb::FieldId& parent) {
    const char *ret = NULL;
    switch(value_GetEnum(parent)) {
        case wsdb_FieldId_code             : ret = "code";  break;
        case wsdb_FieldId_reason           : ret = "reason";  break;
        case wsdb_FieldId_local_only       : ret = "local_only";  break;
        case wsdb_FieldId_comment          : ret = "comment";  break;
        case wsdb_FieldId_value            : ret = "value";  break;
    }
    return ret;
}

// --- wsdb.FieldId.value.Print
// Convert value to a string. First, attempt conversion to a known string.
// If no string matches, print value as a numeric value.
void wsdb::value_Print(const wsdb::FieldId& parent, algo::cstring &lhs) {
    const char *strval = value_ToCstr(parent);
    if (strval) {
        lhs << strval;
    } else {
        lhs << parent.value;
    }
}

// --- wsdb.FieldId.value.SetStrptrMaybe
// Convert string to field.
// If the string is invalid, do not modify field and return false.
// In case of success, return true
bool wsdb::value_SetStrptrMaybe(wsdb::FieldId& parent, algo::strptr rhs) {
    bool ret = false;
    switch (elems_N(rhs)) {
        case 4: {
            switch (u64(algo::ReadLE32(rhs.elems))) {
                case LE_STR4('c','o','d','e'): {
                    value_SetEnum(parent,wsdb_FieldId_code); ret = true; break;
                }
            }
            break;
        }
        case 5: {
            switch (u64(algo::ReadLE32(rhs.elems))|(u64(rhs[4])<<32)) {
                case LE_STR5('v','a','l','u','e'): {
                    value_SetEnum(parent,wsdb_FieldId_value); ret = true; break;
                }
            }
            break;
        }
        case 6: {
            switch (u64(algo::ReadLE32(rhs.elems))|(u64(algo::ReadLE16(rhs.elems+4))<<32)) {
                case LE_STR6('r','e','a','s','o','n'): {
                    value_SetEnum(parent,wsdb_FieldId_reason); ret = true; break;
                }
            }
            break;
        }
        case 7: {
            switch (u64(algo::ReadLE32(rhs.elems))|(u64(algo::ReadLE16(rhs.elems+4))<<32)|(u64(rhs[6])<<48)) {
                case LE_STR7('c','o','m','m','e','n','t'): {
                    value_SetEnum(parent,wsdb_FieldId_comment); ret = true; break;
                }
            }
            break;
        }
        case 10: {
            switch (algo::ReadLE64(rhs.elems)) {
                case LE_STR8('l','o','c','a','l','_','o','n'): {
                    if (memcmp(rhs.elems+8,"ly",2)==0) { value_SetEnum(parent,wsdb_FieldId_local_only); ret = true; break; }
                    break;
                }
            }
            break;
        }
    }
    return ret;
}

// --- wsdb.FieldId.value.SetStrptr
// Convert string to field.
// If the string is invalid, set numeric value to DFLT
void wsdb::value_SetStrptr(wsdb::FieldId& parent, algo::strptr rhs, wsdb_FieldIdEnum dflt) {
    if (!value_SetStrptrMaybe(parent,rhs)) value_SetEnum(parent,dflt);
}

// --- wsdb.FieldId.value.ReadStrptrMaybe
// Convert string to field. Return success value
bool wsdb::value_ReadStrptrMaybe(wsdb::FieldId& parent, algo::strptr rhs) {
    bool retval = false;
    retval = value_SetStrptrMaybe(parent,rhs); // try symbol conversion
    if (!retval) { // didn't work? try reading as underlying type
        retval = i32_ReadStrptrMaybe(parent.value,rhs);
    }
    return retval;
}

