// --- wsdb.Status..ReadFieldMaybe
bool wsdb::Status_ReadFieldMaybe(wsdb::Status& parent, algo::strptr field, algo::strptr strval) {
    bool retval = true;
    wsdb::FieldId field_id;
    (void)value_SetStrptrMaybe(field_id,field);
    switch(field_id) {
        case wsdb_FieldId_code: {
            retval = u32_ReadStrptrMaybe(parent.code, strval);
        } break;
        case wsdb_FieldId_reason: {
            retval = algo::Smallstr32_ReadStrptrMaybe(parent.reason, strval);
        } break;
        case wsdb_FieldId_local_only: {
            retval = bool_ReadStrptrMaybe(parent.local_only, strval);
        } break;
        case wsdb_FieldId_comment: {
            retval = algo::Comment_ReadStrptrMaybe(parent.comment, strval);
        } break;
        default: {
            retval = false;
            algo_lib::AppendErrtext("comment", "unrecognized attr");
        } break;
    }
    if (!retval) {
        algo_lib::AppendErrtext("attr",field);
    }
    return retval;
}

// --- wsdb.Status..ReadStrptrMaybe
// Read fields of wsdb::Status from an ascii string.
// The format of the string is an ssim Tuple
bool wsdb::Status_ReadStrptrMaybe(wsdb::Status &parent, algo::strptr in_str) {
    bool retval = true;
    retval = algo::StripTypeTag(in_str, "wsdb.status") || algo::StripTypeTag(in_str, "wsdb.Status");
    ind_beg(algo::Attr_curs, attr, in_str) {
        retval = retval && Status_ReadFieldMaybe(parent, attr.name, attr.value);
    }ind_end;
    return retval;
}

// --- wsdb.Status..Print
// print string representation of ROW to string STR
// cfmt:wsdb.Status.String  printfmt:Tuple
void wsdb::Status_Print(wsdb::Status& row, algo::cstring& str) {
    algo::tempstr temp;
    str << "wsdb.status";

    u32_Print(row.code, temp);
    PrintAttrSpaceReset(str,"code", temp);

    algo::Smallstr32_Print(row.reason, temp);
    PrintAttrSpaceReset(str,"reason", temp);

    bool_Print(row.local_only, temp);
    PrintAttrSpaceReset(str,"local_only", temp);

    algo::Comment_Print(row.comment, temp);
    PrintAttrSpaceReset(str,"comment", temp);
}

