// --- lib_netio.FDb.var.Find
// Look up row by row id. Return NULL if out of range
inline lib_netio::FVar* lib_netio::var_Find(u64 t) {
    lib_netio::FVar *retval = NULL;
    if (LIKELY(u64(t) < u64(_db.var_n))) {
        u64 x = t + 1;
        u64 bsr   = algo::u64_BitScanReverse(x);
        u64 base  = u64(1)<<bsr;
        u64 index = x-base;
        retval = &_db.var_lary[bsr][index];
    }
    return retval;
}

// --- lib_netio.FDb.var.Last
// Return pointer to last element of array, or NULL if array is empty
inline lib_netio::FVar* lib_netio::var_Last() {
    return var_Find(u64(_db.var_n-1));
}

